mapper.update:
UPDATE command
SET event_id = #{eventId}, title = #{title}, content = #{content}, commander_id = #{commanderId},
    commander_name = #{commanderName}, executor_id = #{executorId}, executor_name = #{executorName},
    priority = #{priority}, status = #{status}, execute_time = #{executeTime}, complete_time = #{completeTime}
WHERE id = #{id}
